import React, { Component } from 'react'
import { connect } from 'react-redux'
import { Row, Col, Icon, Button, Input, Tooltip } from 'antd'

import { pushChatMsg, recvChatMsg, modifyContacts } from '../../../redux/actions'
// import { sendMsg } from '../../../utils/websocket'
import FaceEmjoy from '../../../components/message/face'

class ChatTextarea extends Component {

    state = { message: '', showFace: false }

    componentDidMount(){
        document.onclick = () => this.setState({ showFace: false })
    }

    handleTextArea = (name, e) => {
        this.setState({ [name]: e.target.value })
    }

    render() {
        const { showFace } = this.state
        return (
            <section style={{ position: "relative" }}>
                <Row className="chat-tools">
                    <Col span={18}>
                        <Tooltip title="ÂèëÈÄÅË°®ÊÉÖ">
                            <Icon type="smile" onClick={e => this.showFace(e)} />
                        </Tooltip>
                        <Tooltip title="ÂèëÈÄÅÂõæÁâá"><Icon type="picture" /></Tooltip>
                        <Tooltip title="ÂèëÈÄÅ‰ª£Á†ÅÁâáÊÆµ"><Icon type="code" /></Tooltip>
                        <Tooltip title="ÂèëÈÄÅÈìæÊé•"><Icon type="link" /></Tooltip>
                        <FaceEmjoy parent={ this } showFace={showFace} />
                    </Col>
                    <Col span={6} style={{ textAlign: "right" }}>
                        <Tooltip title="ËßÜÈ¢ëËÅäÂ§©"><Icon type="video-camera" style={{ marginRight: 10 }} /></Tooltip>
                        <Tooltip title="Êà™Âõæ"><Icon type="scissor" /></Tooltip>
                    </Col>
                </Row>
                <Input.TextArea className="chat-textarea"
                       onChange={e => this.handleTextArea('message', e) } placeholder="ËæìÂÖ•‰ø°ÊÅØ..."
                       onPressEnter={e => this.sendChatMess(e) }
                       value={this.state.message}
                />
                <Button style={{ float: "right" }} onClick={ e => this.sendChatMess(e) }>ÂèëÈÄÅ</Button>
            </section>
        )
    }

    showFace = e => {
        e.nativeEvent.stopImmediatePropagation()
        const {showFace} = this.state
        this.setState({ showFace: !showFace })
    }

    // Êé•Êî∂Â≠êÁªÑ‰ª∂‰º†ÂÄº
    getFaceItem = (object, faceEmjoy) => {
        let { message } = this.state
        message += faceEmjoy + " "
        this.setState({ message })
    }

    sendChatMess = e => {
        e.preventDefault();
        const { message } = this.state
        if(!message) return
        this.setState({ message: '' })

        // Êé®ÈÄÅsocketÊ∂àÊÅØ
        const { chat: { chatUserInfo }} = this.props
        // sendMsg(message, chatUserInfo.id)

        // ËøôÊòØËá™Â∑±ÁöÑÊ∂àÊÅØ
        let { user: {userInfo, contacts} } = this.props
        this.props.pushChatMsg({
            id: userInfo.id,
            to_id: chatUserInfo.id,
            avatar: userInfo.avatar,
            message: message,
            position: "right"
        })

        const randomText = ["Â∑•ÂÖ∑‰∫∫‰∏ÄÂè∑‰∏∫ÊÇ®ÊúçÂä°ÔΩû", `‰Ω†Â•ΩÔºåÊàëÊòØ${chatUserInfo.nickname}ÔºåÂæàÈ´òÂÖ¥ËÆ§ËØÜ‰Ω†`, "oh godÔºå‰Ω†Áªà‰∫éÊÉ≥Ëµ∑Êàë‰∫ÜÂïä", "ÊòéÂ§©‰∏ÄËµ∑ÂéªÁà¨Â±±Èú≤Ëê•ÂïäÔºåÊúâÁ©∫ÂêóÔºü", "helloÔºåÊàëÁé∞Âú®Âú®Â∑•‰ΩúÔºå‰Ω†Âë¢Ôºü", "üåπüåπ ÊÄé‰πàËØ¥Âë¢ÔºåÈÇ£Â∞±Âú®‰∏ÄËµ∑ÂêßÔΩû"]
        const lastMessage = randomText[parseInt(Math.random()*randomText.length)]
        this.props.recvChatMsg({
            id: chatUserInfo.id,
            to_id: userInfo.id,
            avatar: chatUserInfo.avatar,
            message: lastMessage,
            position: "left"
        })

        if(contacts) {
            const index = contacts.findIndex(user => user.id === chatUserInfo.id)
            if(index >= 0) {
                let info = contacts[index]
                info.last_mess = lastMessage; info.time = "ÂàöÂàö"
                if(index > 0) {
                    contacts.unshift(info)
                    contacts = contacts.slice(index, 1);
                }
                this.props.modifyContacts(contacts)
            }
        }
    }
}

export default connect(
    state => ({ chat: state.chat, user: state.user }),
    { pushChatMsg, recvChatMsg, modifyContacts }
)(ChatTextarea)
