import React, { Component } from 'react'

const emjoy = 'ğŸ˜„ ğŸ˜Š ğŸ˜ƒ ğŸ˜‰ ğŸ˜ ğŸ˜˜ ğŸ˜š ğŸ˜³ ğŸ˜ ğŸ˜Œ ğŸ˜œ ğŸ˜ ğŸ˜’ ğŸ˜ ğŸ˜“ ğŸ˜” ğŸ˜– ğŸ˜¥ ğŸ˜° ğŸ˜¨ ğŸ˜£ ğŸ˜¢ ğŸ˜­ ğŸ˜‚ ğŸ˜± ğŸ˜  ğŸ˜ª ğŸ˜· ğŸ˜‡ ğŸ˜‹ ğŸ˜§ ğŸ˜¦ ğŸ˜¯ ğŸ˜µ ğŸ˜› ğŸ˜¶ ğŸ˜ ğŸ˜… ğŸ˜Ÿ ğŸ‘ ğŸ‘ ğŸ‘ ğŸ™ âœŠ â— ğŸ” ğŸš³ ğŸ“µ ğŸˆ² ğŸ†™ ğŸ² ğŸ’£ ğŸ’¥ ğŸ² ğŸ’° ğŸ”” ğŸ”• ğŸ’© ğŸŒ¹';
const emjoyList = emjoy.split(" ");

class FaceEmjoy extends Component {
    render() {
        const { showFace } = this.props
        return <div className="face-emjoy" style={{ display: showFace ? 'block' : 'none' }}>
            { emjoyList.map(item => <span className="emjoy" onClick={e => this.sendFace(e, item)} key={item}>{ item }</span>) }
        </div>
    }

    sendFace = (e, face) => {
        e.nativeEvent.stopImmediatePropagation()
        this.props.parent.getFaceItem(this, face)
    }
}
export default FaceEmjoy
